import React, { useState } from 'react'
import AddIcon from '@material-ui/icons/Add';
import '../../styles/Upload.css'

import { makeStyles } from '@material-ui/core/styles';
import Modal from '@material-ui/core/Modal'
import { dblClick } from '@testing-library/user-event/dist/click';

function getModalStyle(){
  return{
    top:'50%',
    left:'50%',
    transform:'translate(-50%,-50%)'
  };
}
const useStyles = makeStyles((theme) => ({
  paper:{
    position: 'absolute',
    width: 400,
    backgroundColor: theme.palette.background.paper,
    border: '2px solid #000',
    boxShadow: theme.shadows[5],
    padding: theme.spacing(2,4,3),
  }
}));

const Upload = () => {
  const classes = useStyles();
  const [modalStyle] =useState(getModalStyle);
  const [open, setOpen] = useState(false);
  const [file, setfile] = useState(null)
  const [uploading,setUploading] = useState(false)

  const handleOpen =() =>{
    setOpen(true);
  }

  const handleClose = () =>{
    setOpen(false);
  }

  const handleChange = () =>{
    if(e.target.files[0]){
      setfile(e.target.files[0])
    }
  }

  const handleUpload =()=>{
    setUploading(true)

    storage.ref(`file/${file.name}`).put(file).then(snapshot=>{
      console.log(snapshot)

      storage.ref('file').child(file.name).getDownloadURL.then(url=>{
        db.collection('myFile').add({
          // timestamp: firebase.firestore.Fieldvalue.serverTimestamp(),
          caption: file.name,
          fileUrl: url,
          size: snapshot._delegate.bytesTransferred,
        })
        setUploading(false)
        setOpen(false)
        setfile(null)
      })
      storage.ref('files').child(file.name).getMetadata().then(meta => {
        console.log(meta.size)
      })
    })
  }

  return (
    <div className='upload'>
        <div className="upload__container" onClick={handleOpen}>
            <AddIcon fontSize='large'/>
            <p>Upload</p>
        </div>
        <Modal>
              open={open}
              onClose={handleClose}
              aria-labelledby="simple-modal-title"
              aria-aria-describedby="simple-modal-description"

              <div style={modalStyle} className={classes.paper}>
                <p> Select files you want to upload</p>
                {
                  upload ?(
                    <p>Uploading....</p>
                  ):(
                    <>
                        <input type="file" onChange={handleChange}/>
                        <button onClick={handleUpload}>Upload</button>
                    </>
                  )
                }
              </div>
          </Modal>
    </div>
  )
}

export default Upload
